import React from "react";


export default class Users extends React.Component {
  state = {
    loading: true,
    person: null
  };

  /*
  async componentDidMount() {
    const url = "http://localhost:3001/api/users";
    const response = await fetch(url);
    const allUsers = await response.json();
    this.setState({ allUsers, loading: false });
  }
  */

  async componentDidMount(){
    const url = "http://localhost:3001/api/users";
    this.setState({isLoading: true})

    const response = await fetch(url)

    if(response.ok){
      const users = await response.json()
      console.log(users)
      this.setState({users, isLoading:false})
    }else{
      this.setState({isError:true, isLoading:false})
    }
  }


  renderTableHeader = () =>{
    return Object.keys(this.state.users[0]).map(attr =>{<th key={attr}>
      {attr.toUpperCase()}
    </th>

    })
  }

  renderTableRows = () =>{
    
  }

  render(){
    const {users, isLoading, isError} = this.state

    if(isLoading){
      return <div>Loading...</div>
    }

    if(isError){
      return <div>ERROR!</div>
    }

    return users.length > 0
    ?(
      <table>
        <thead>
          <tr>
            {this.renderTableHeader()}
          </tr>
        </thead>
        <tbody>
          {this.renderTableRows()}
        </tbody>
      </table>

    ):(
      <div>No users</div>
    )

  }



  /*
  render() {
    if (this.state.loading) {
      return <div>loading...</div>;
    }

    if (!this.state.allUsers) {
      return <div>No users were added to the DB!</div>;
    }

    
    return (
      <div>
        <div>{JSON.stringify(this.state.allUsers)}</div>
      </div>
    );
 
    return (
      <div>
        {this.state.map((userInfo) => {
          return <h1>{userInfo.name}</h1>
        })
        }
      </div>
    )
    */




  }
