import "./App.css";
import axios from "axios";
import { useEffect, useState } from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import React, { Component }  from 'react';
import { Radio } from "@material-ui/core";

const StyledTableCell = withStyles((theme) => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles((theme) => ({
  root: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover,
    },
  },
}))(TableRow);

const useStyles = makeStyles({
  table: {
    minWidth: 700,
  },
});



const Projects = () => {
  const classes = useStyles();
  const [product, setProduct] = useState([]);
  const [search, setSearch] = useState("");


  

  const getProductData = async () => {
    try {
      const data = await axios.get(
        "http://localhost:3001/api/projects"
      );
      console.log(data.data);
      setProduct(data.data);
    } catch (e) {
      console.log(e);
    }
  };

  useEffect(() => {
    getProductData();
  }, []);

  return (
    <div className="Projects">
      <h1>Projects</h1>
      <input
        type="text"
        placeholder="Search here"
        onChange={(e) => {
          setSearch(e.target.value);
        }}
      />

      {/* {product
        .filter((project) => {
          if (search == "") {
            return project;
          } else if (project.name.toLowerCase().includes(search.toLowerCase())) {
            return project;
          }
        })
        .map((project) => {
          return (
            <p>
              {project.name} - {project.price}
            </p>
          );
        })} */}

      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="customized table">
          <TableHead>
            <TableRow>
              <StyledTableCell>Project Name</StyledTableCell>
              <StyledTableCell align="center">People Working</StyledTableCell>
              <StyledTableCell align="right">Project Due Date</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {product
              .filter((project) => {
                if (search == "") {
                  return project;
                } else if (
                  project.title.toLowerCase().includes(search.toLowerCase())
                ) {
                  return project;
                }
              })
              .map((project) => {
                return (
                  <StyledTableRow key={project.id}>
                    <StyledTableCell component="th" scope="row">
                      <label>
                        <input type="radio" 
                                value={project.title}></input>
                      </label>
                      {project.title}
                    </StyledTableCell>
                    <StyledTableCell align="center">
                      {project.people}
                    </StyledTableCell>
                    <StyledTableCell align="right">
                      {project.dueDate}
                    </StyledTableCell>
                  </StyledTableRow>
                );
              })}
          </TableBody>
        </Table>
      </TableContainer>
      <div style={{ marginTop: 15, display: "flex" }}>
        <button
          style={{ marginLeft: 0 }}
        >
          Create New User
        </button>
        <button
          style={{ marginLeft: "auto" }}
        >
          Edit User
        </button>
        <button
          style={{ marginLeft: "auto" }}
        >
          Delete User
        </button>
      </div>
    </div>
  );
};

export default Projects;
